mapper.select:
/* archiveMapper.selectCategoryUid */
SELECT 
	CATEGORY_UID 
FROM 
	`CATEGORY` 
WHERE 
	REGISTRATION_USER 	= #{userUid}
	AND URL_PATH		= #{categoryUrlPath}
	AND FLAG_DELETED	= 'N'

/* archiveMapper.checkArticleExistence */
SELECT EXISTS (
	SELECT
		1
	FROM
		(
		SELECT 
			ROW_NUMBER() OVER (ORDER BY REGISTRATION_DATE ASC) AS NUM
			, FLAG_DELETED
		FROM 
			ARTICLE
		WHERE
			REGISTRATION_USER 	= #{userUid}
			AND CATEGORY_UID	= #{categoryUid}
		) RSLT
	WHERE
	   RSLT.NUM = #{articleNumber}
) AS FLAG_EXISTENCE

		/* archiveMapper.selectArticleUid */
		SELECT
			RSLT.ARTICLE_UID AS articleUid
		FROM
			(
			SELECT
    			ROW_NUMBER() OVER (PARTITION BY CATEGORY_UID ORDER BY REGISTRATION_DATE ASC) 	AS NUM
				, ARTICLE_UID
			FROM
    			ARTICLE
			WHERE
				REGISTRATION_USER 	= #{userUid}
    			AND CATEGORY_UID 	= #{categoryUid}
			) RSLT
		WHERE
			RSLT.NUM = #{articleNumber}

/* archiveMapper.selectArticleListCount */ SELECT COUNT(*) FROM ( SELECT ROW_NUMBER() OVER (ORDER BY RSLT1.REGISTRATION_DATE DESC) AS PAGING_INDEX , RSLT1.NUM AS num , RSLT1.ARTICLE_UID AS articleUid , RSLT1.TITLE AS title
, REGEXP_REPLACE(RSLT1.CONTENT, '<[^>]+>', '') AS content
, RSLT1.URL_PATH AS thumbnailUrlPath , GET_ARTICLE_URL(RSLT1.ARTICLE_UID, RSLT1.NUM) AS articleUrlPath , RSLT1.CATEGORY_UID , RSLT1.CATEGORY_NAME AS categoryName , RSLT1.REGISTRATION_USER , DATE_FORMAT(RSLT1.REGISTRATION_DATE, '%Y. %m. %d') AS registrationDate , RSLT1.FLAG_DELETED FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY A.CATEGORY_UID ORDER BY A.REGISTRATION_DATE ASC) AS NUM , A.ARTICLE_UID , A.TITLE , A.CONTENT , F.URL_PATH , C.CATEGORY_UID , C.CATEGORY_NAME , A.REGISTRATION_USER , A.REGISTRATION_DATE , A.FLAG_DELETED , A.FLAG_ACTIVATED FROM ARTICLE A LEFT JOIN `FILE` F ON F.FILE_UID = A.THUMBNAIL_UID LEFT JOIN CATEGORY C ON C.CATEGORY_UID = A.CATEGORY_UID
WHERE A.REGISTRATION_USER = #{currentArchiveUserUid}
AND A.CATEGORY_UID = #{currentCategoryUid}
) RSLT1 WHERE RSLT1.FLAG_DELETED = 'N' AND RSLT1.FLAG_ACTIVATED = 'Y'
AND RSLT1.TITLE LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY RSLT1.REGISTRATION_DATE DESC ) RSLT2

/* archiveMapper.selectArticleList */ SELECT RSLT2.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY RSLT1.REGISTRATION_DATE DESC) AS PAGING_INDEX , RSLT1.NUM AS num , RSLT1.ARTICLE_UID AS articleUid , RSLT1.TITLE AS title
, REGEXP_REPLACE(REGEXP_REPLACE(RSLT1.CONTENT, '<[^>]+>', ''), '&nbsp;', '') AS content
, RSLT1.URL_PATH AS thumbnailUrlPath , GET_ARTICLE_URL(RSLT1.ARTICLE_UID, RSLT1.NUM) AS articleUrlPath , RSLT1.CATEGORY_UID , RSLT1.CATEGORY_NAME AS categoryName , RSLT1.REGISTRATION_USER , DATE_FORMAT(RSLT1.REGISTRATION_DATE, '%Y. %m. %d') AS registrationDate , RSLT1.FLAG_DELETED FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY A.CATEGORY_UID ORDER BY A.REGISTRATION_DATE ASC) AS NUM , A.ARTICLE_UID , A.TITLE , A.CONTENT , F.URL_PATH , C.CATEGORY_UID , C.CATEGORY_NAME , A.REGISTRATION_USER , A.REGISTRATION_DATE , A.FLAG_DELETED , A.FLAG_ACTIVATED FROM ARTICLE A LEFT JOIN `FILE` F ON F.FILE_UID = A.THUMBNAIL_UID LEFT JOIN CATEGORY C ON C.CATEGORY_UID = A.CATEGORY_UID
WHERE A.REGISTRATION_USER = #{currentArchiveUserUid}
AND A.CATEGORY_UID = #{currentCategoryUid}
) RSLT1 WHERE RSLT1.FLAG_DELETED = 'N' AND RSLT1.FLAG_ACTIVATED = 'Y'
AND RSLT1.TITLE LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY RSLT1.REGISTRATION_DATE DESC ) RSLT2 WHERE PAGING_INDEX > ${dataPagingIndex} ORDER BY PAGING_INDEX ASC LIMIT #{dataCountPerPage}
